!function (t) {
    "use strict";

    /* -------------------------------------------------------- */
    /* - Color Switcher
    /* -------------------------------------------------------- */
    t('.switcher-btn').on('click', function () {
        t('.color-switcher').toggleClass('active');
    });

    t(".theme-buttons-container ul li").on("click", function () {
        var s = t(this).data("path");
        t("#color-switcher").attr("href", s), t(this).parent().find("li").removeClass("active"), t(this).addClass("active")
    });

}(jQuery);